import { createStore } from "redux"
import { combineReducers} from "redux"

// const reducer = (state=[],action) =>{
//     return state
// }

const initialState = {
    cart:[
        {
            product:'bread 700g',
            quality:2,
            unitCost:90
        },
        {
            product:'milk 500ml',
            quality:2,
            unitCost:90
        }
    ]
}

const ADD_TO_CART = 'ADD_TO_CART'


const productsReducer = (state=[],action)=>{
    return state
}
const cartReducer = (state=initialState,action)=>{
    // return state
    switch(action.type){
        case ADD_TO_CART:{
            return{
                ...state,
                cart:[...state.cart,action.payload]
            }
        }

        default:
            return state
    }
}


function addToCart(product,quality,unitCost){
    return {
        type:ADD_TO_CART,
        payload:{product,quality,unitCost}
    }
}

const allReducers = {
    products:productsReducer,
    shoppingCart:cartReducer
}

const rootReducer = combineReducers(allReducers)

const store = createStore(rootReducer)

console.log('initial',store.getState())

let unsubscrible = store.subscribe(()=>{
    console.log(store.getState())
})

store.dispatch(addToCart('coffee 500ml',1,2))
store.dispatch(addToCart('flower 1',1,2))
store.dispatch(addToCart('juice',1,2))



unsubscrible()




function About() {
    return <div>about</div>
}

export default About
